alpha = ts_quantile(combined_cashflow, 57, driver = 'gaussian');
group_zscore(alpha, industry);

#Split weights across combined cash flow
combined_cashflow = (0.64 * cashflow_op / enterprise_value + 0.36 * est_fcf / enterprise_value);
