#Split weights across combined cash flow
combined_cashflow = (0.64 * cashflow_op / enterprise_value + 0.36 * est_fcf / enterprise_value);
#Smoothing the combined_cashflow prior to Gaussian quantile to reduce noise
combined_cashflow_smooth = ts_mean(combined_cashflow, 10);
alpha = ts_quantile(combined_cashflow_smooth, 57, driver = 'gaussian');
group_rank(alpha, industry);

